def make_matrix(R, t):
    assert (batch_size := len(R)) == len(t)
    matrix = torch.zeros(batch_size, 4, 4).to(R)
    matrix[..., :3, :3] = R
    matrix[..., :3, 3] = t
    matrix[..., -1, -1] = 1.0
    return matrix

